mapper.insert:
INSERT INTO board
SET title = #{title}
, contents = #{contents}
, view = #{view}

mapper.select:
SELECT stored_pic AS boardPic, ipics
FROM board_pic
WHERE iboard = #{iboard}

mapper.update:
UPDATE board
SET view = view + 1
WHERE iboard = #{iboard};

UPDATE board
SET title = #{title}
, content = #{content}
WHERE iboard = #{iboard}

UPDATE board_pic
SET

    stored_pic = #{storedPic}

WHERE iboard = #{iboard}

UPDATE board
SET status = #{status}
WHERE iboard = #{iboard} and iuser = #{iuser}

mapper.insert:
INSERT INTO board_like
SET iboard = #{iboard}
, iuser = #{loginIuser}
, created_at = #{createdAt}

mapper.delete:
DELETE FROM board_like
WHERE iboard = #{iboard}
and iuser = #{loginIuser}

mapper.select:
select count(iboard) AS totalBoardCount
from board
where status = #{status};
