mapper.insert:

INSERT INTO chat
SET last_msg = NULL,
iproduct = #{iproduct}

INSERT INTO chat_user SET
ichat = #{ichat},
iuser = #{iuser}

SELECT IFNULL(MAX(seq), 0) + 1 FROM chat_message WHERE ichat = #{ichat}
INSERT INTO chat_message SET ichat = #{ichat}, seq = #{seq}, iuser = #{loginedIuser},
msg = #{msg}

mapper.select:
SELECT SUM(CASE WHEN A.istatus + B.istatus = 0 THEN 1 ELSE 0 END) AS existEnableRoom
FROM chat_user A
INNER JOIN chat_user B
ON A.ichat = B.ichat
INNER JOIN chat C
ON B.ichat = C.ichat
WHERE A.iuser = #{loginedIuser}
AND B.iuser = #{otherPersonIuser}
AND C.iproduct = #{iproduct}

mapper.update:
UPDATE chat
SET
last_msg = #{message}
WHERE ichat = #{ichat}

UPDATE chat_user
SET istatus = -1
WHERE iuser = #{iuser} AND ichat = #{ichat} ;

mapper.select:
SELECT iuser, nick, stored_pic AS storedPic
FROM user
WHERE iuser = #{iuser}

SELECT istatus
FROM chat_user
WHERE ichat = #{ichat} AND iuser != #{loginedIuser};
