mapper.select:
SELECT distinct pa.iproduct, pa.ipayment, pp.stored_pic as ProStoredPic, pa.deposit, pa.total_price as totalPrice, pa.rental_end_date as rentalEndDate , pa.rental_start_date as rentalStartDate, pa.duration, pa.created_at as createdAt, pai.`status`, pr.iuser, u.nick, u.stored_pic as userPic, pr.title, pr.main_category AS mainCategoryy, pr.sub_category AS subCategoryy from payment pa LEFT JOIN payment_info pai ON pa.ipayment = pai.ipayment LEFT JOIN product pr ON pa.iproduct = pr.iproduct LEFT JOIN prod_pics pp ON pp.iproduct = pa.iproduct JOIN `user` u ON u.iuser = pr.iuser WHERE pa.iuser = #{iuser}
and pai.`status` in ('RESERVED', 'ACTIVATED', 'CANCELED')
and pai.`status` in ('EXPIRED', 'COMPLETED', 'HIDDEN')
GROUP BY pa.ipayment ORDER BY pa.ipayment DESC LIMIT #{startIdx}, #{rowCount}

SELECT distinct pr.iproduct, pa.ipayment, pp.stored_pic as ProStoredPic, pa.deposit, pa.total_price as totalPrice, pa.rental_end_date as rentalEndDate , pa.rental_start_date as rentalStartDate, pa.duration, pa.created_at as createdAt, pai.`status`, pa.iuser, u.nick, u.stored_pic as userPic, pr.title, pr.main_category AS mainCategoryy, pr.sub_category AS subCategoryy FROM `user` u JOIN product pr ON u.iuser = pr.iuser LEFT JOIN prod_pics pp ON pp.iproduct = pr.iproduct JOIN payment pa ON pa.iproduct = pr.iproduct LEFT JOIN payment_info pai ON pa.ipayment = pai.ipayment WHERE pr.iuser = #{iuser}
and pai.`status` in ('RESERVED', 'ACTIVATED', 'CANCELED')
and pai.`status` in ('EXPIRED', 'COMPLETED', 'HIDDEN')
GROUP BY pr.iproduct ORDER BY pr.iproduct DESC LIMIT #{startIdx}, #{rowCount}
